import React from 'react';
import logo from './logo.svg';
import Header from './header';
import './App.css';

import Modal from 'react-modal';

const customStyles = {
	content: {
		top: '50%',
		left: '50%',
		right: 'auto',
		bottom: 'auto',
		marginRight: '-50%',
		transform: 'translate(-50%, -50%)',
	},
};

Modal.setAppElement('#root');

export default class App extends React.Component {
	constructor() {
		super();
		this.state = {
			todos: [],
			modalIsOpened: false,
			modal2: 0,
			newaktivitas: '',
			newjam: '',
			data: [],
		};
		this.openModal = this.openModal.bind(this);
		this.afterOpenModal = this.afterOpenModal.bind(this);
		this.closeModal = this.closeModal.bind(this);
	}

	openModal() {
		this.setState({ modalIsOpened: true });
	}
	afterOpenModal() {
		// references are now sync'd and can be accessed.
		this.subtitle.style.color = '#f00';
		this.refs.editjam.value = this.state.data.jam;
		this.refs.editaktivitas.value = this.state.data.aktivitas;
	}
	closeModal() {
		this.setState({ modalIsOpened: false });
	}

	submitChange = event => {
		this.setState({ newaktivitas: this.refs.editaktivitas.value, newjam: this.refs.editjam.value });
		// let dat = [this.state.newjam, this.state.newaktivitas]
		// this.state.todos.splice(this.state.data, 1, dat);
		// this.setState({ todos: this.state.todos })

		this.closeModal();
	};

	addTodo = e => {
		e.preventDefault(); // preventDefault untuk mencegah data yg belum diproses

		let jam = this.refs.jam.value;
		let aktivitas = this.refs.aktivitas.value;

		this.state.todos.push({ jam, aktivitas });
		this.setState({ todos: this.state.todos });

		this.refs.formulir.reset();
		this.refs.jam.focus();
	};

	editTodo(index) {
		this.openModal();
		this.setState({ modal2: index });
		// this.state.todos.splice(index, 1, this.refs.aktivitas.value)
	}

	removeTodo = i => {
		this.state.todos.splice(i, 1);
		this.setState({ todos: this.state.todos });
	};
	render() {
		return (
			<div>
				<br />
				<div className="App">
					<Header />
				</div>
				<form ref="formulir" className="form-inline">
					<div className="form-group mx-sm-3 mb-2">
						<input type="time" className="form-control" ref="jam" placeholder="jam aktivitas" />
						<input type="text" className="form-control" ref="aktivitas" placeholder="jenis aktifitas" />
					</div>
					<div className="form-group mb-2">
						<button onClick={this.addTodo} className="btn btn-info">
							simpan
						</button>
					</div>
				</form>
				<Modal
					isOpen={this.state.modalIsOpened}
					onAfterOpen={this.afterOpenModal}
					onRequestClose={this.closeModal}
					style={customStyles}
					contentLabel="Edit"
				>
					<div>
						<h2 ref={subtitle => (this.subtitle = subtitle)}>Hello</h2>
						<form ref={editform => (this.editform = editform)}>
							<div className="form-group">
								<input type="time" ref="editjam" className="form-control" />
							</div>
							<div className="form-group">
								<input type="text" ref="editaktivitas" className="form-control" />
							</div>
							<div className="form-group mb-2 float-right">
								<button onClick={this.submitChange.bind(this)} type="submit" className="btn btn-info">
									simpan
								</button>
							</div>
						</form>
					</div>
				</Modal>
				<hr />
				<div>
					<ul className="list-group">
						{this.state.todos.map((data, i) => (
							<li className="list-group-item" key={i}>
								<div>
									{data.jam} : {data.aktivitas}
									<button onClick={() => [this.editTodo(i), this.setState({ data: data })]} className="btn btn-outline-primary mx-sm-3 mb-2">
										Edit
									</button>
									<button onClick={() => this.removeTodo(i)} className="btn btn-outline-danger mx-sm-3 mb-2">
										Hapus
									</button>
								</div>
							</li>
						))}
					</ul>
					<h2>
						OYI : {this.state.newaktivitas}: {this.state.newjam}
					</h2>
					{this.state.todos.splice(this.state.data, 1, [this.state.newjam, this.state.newaktivitas])}
					{this.setState({ todos:this.state.todos })}
				</div>
			</div>
		);
	}
}
