import React from 'react';
import logo from './logo.svg';
import Header from './header';
import './App.css';

import Modal from 'react-modal';

const customStyles = {
	content: {
		top: '50%',
		left: '50%',
		right: 'auto',
		bottom: 'auto',
		marginRight: '-50%',
		transform: 'translate(-50%, -50%)',
	},
};

Modal.setAppElement("#root")

export default class App extends React.Component {
	constructor() {
		super();
		this.state = {
			todos: [],
			modalIsOpened: false,
			modal2: 0,
			newaktivitas:'',
			newjam:''
		};
		this.openModal = this.openModal.bind(this);
		this.afterOpenModal = this.afterOpenModal.bind(this);
		this.closeModal = this.closeModal.bind(this);
	}


	openModal() {
		this.setState({ modalIsOpened: true });
	}
	afterOpenModal() {
		// references are now sync'd and can be accessed.
		this.subtitle.style.color = '#f00';

		this.editaktivitas.value = this.state.todos[this.state.modal2].aktivitas;
		this.editjam.value = this.state.todos[this.state.modal2].jam;
		this.editjam.onchange = this.setState({ newaktivitas: this.editaktivitas.value });
		this.editaktivitas.onchange = this.setState({ newjam: this.editjam.value });

	}

	// processedit(){
	// 	let New = [this.state.newjam, this.state.newaktivitas];
	// 	// this.state.todos.splice(this.state.modal2, 1, New);
	// 	this.state.todos.splice(this.state.modal2, 1, [this.state.newjam, this.state.newaktivitas]);
	// }
	closeModal() {
		this.setState({ modalIsOpened: false });
	}

	addTodo = e => {
		e.preventDefault(); // preventDefault untuk mencegah data yg belum diproses

		let jam = this.refs.jam.value;
		let aktivitas = this.refs.aktivitas.value;

		this.state.todos.push({ jam, aktivitas });
		this.setState({ todos: this.state.todos });

		this.refs.formulir.reset();
		this.refs.jam.focus();
	};

	editTodo(index) {
		this.openModal()
		this.setState({ modal2: index })
		// this.state.todos.splice(index, 1, this.refs.aktivitas.value)
	};

	removeTodo = i => {
		this.state.todos.splice(i, 1);
		this.setState({ todos: this.state.todos });
	};
	render() {
		return (
			<div>
				<br />
				<div className="App">
					<Header />
				</div>
				<form ref="formulir" className="form-inline">
					<div className="form-group mx-sm-3 mb-2">
						<input type="time" className="form-control" ref="jam" placeholder="jam aktivitas" />
						<input type="text" className="form-control" ref="aktivitas" placeholder="jenis aktifitas" />
					</div>
					<div className="form-group mb-2">
						<button onClick={this.addTodo} className="btn btn-info">
							simpan
						</button>
					</div>
				</form>
				<Modal
					isOpen={this.state.modalIsOpened}
					onAfterOpen={this.afterOpenModal}
					onRequestClose={this.closeModal}
					style={customStyles}
					contentLabel="Edit"
				>
					<div>
						<h2 ref={subtitle => (this.subtitle = subtitle)}>Hello</h2>
						<form ref={editform => (this.editform = editform)} className="form-inline">
							<div className="form-group mx-sm-3 mb-2">
								<input type="time" className="form-control" onChange={this.state.newjam} ref={editjam => (this.editjam = editjam)} placeholder="jam aktivitas" />
								<input type="text" className="form-control" onChange={this.setState} ref={editaktivitas => (this.editaktivitas = editaktivitas)} placeholder="jenis aktifitas" />
							</div>
							<div className="form-group mb-2">
								<button onClick={this.closeModal} type="submit" className="btn btn-info">
									simpan
								</button>
							</div>
						</form>
					</div>
				</Modal>
				<hr />
				<div>
					<ul className="list-group">
						{this.state.todos.map((data, i) => (
							<li className="list-group-item" key={i}>
								<div>
									{data.jam} : {data.aktivitas}
									<button onClick={() => this.editTodo(i)} className="btn btn-outline-primary mx-sm-3 mb-2">
										Edit
									</button>
									<button onClick={() => this.removeTodo(i)} className="btn btn-outline-danger mx-sm-3 mb-2">
										Hapus
									</button>
								</div>
							</li>
						))}
					</ul>
				</div>
			</div>
		);
	}
}
